import { Formik, Form, useField } from "formik";
import * as Yup from "yup";

import React, { useState, useEffect } from "react";

import { useNavigate, useLocation, Link } from "react-router-dom";

import HeaderForEmployer from "./HeaderForEmployer";

export default function AddJobForEmployer() {
  const [data, setData] = useState([]);

  const [selectedFile, setSelectedFile] = useState("");
  const [status, setStatus] = useState("");

  const MyTextInput = ({ label, ...props }) => {
    // useField() returns [formik.getFieldProps(), formik.getFieldMeta()]
    // which we can spread on <input>. We can use field meta to show an error
    // message if the field is invalid and it has been touched (i.e. visited)
    const [field, meta] = useField(props);
    return (
      <>
        <div class="mb-3 mt-3">
          <label htmlFor={props.id || props.name}>{label}</label>
          <input
            className="text-input"
            class="form-control"
            {...field}
            {...props}
          />
          {meta.touched && meta.error ? (
            <div className="error">{meta.error}</div>
          ) : null}
        </div>
      </>
    );
  };

  const MySelect = ({ label, ...props }) => {
    const [field, meta] = useField(props);
    return (
      <div>
        <label htmlFor={props.id || props.name}>{label}</label>
        <select class="form-control" {...field} {...props} />
        {meta.touched && meta.error ? (
          <div className="error">{meta.error}</div>
        ) : null}
      </div>
    );
  };

  return (
    <div className=".container-fluid">
      <div style={{ marginBottom: 50 }}>
        {" "}
        <HeaderForEmployer />
      </div>
      <div class="container mt-3">
        <div
          style={{
            flex: 1,
            justifyContent: "center",
            textAlign: "center",
          }}
        >
          <h1>Add a product</h1>
        </div>
        <Formik
          initialValues={{
            productName: "",
            price: 0,
            brand: "",
            quantity: 0,
            product_detail: "",
          }}
          validationSchema={Yup.object({
            productName: Yup.string()
              .max(50, "Must be 50 characters or less")
              .required("Required"),
            productName: Yup.string().required("Required"),
            price: Yup.number().min(1, "At least 1 USD").required("Invalid"),
            quantity: Yup.number().min(1, "At least 1 ").required("Invalid"),
            brand: Yup.string().required("Required"),
            product_detail: Yup.string().required("Required"),
          })}
          //   onSubmit={(values) => {
          //     var today = new Date(),
          //       date =
          //         today.getFullYear() +
          //         "-" +
          //         (today.getMonth() + 1) +
          //         "-" +
          //         today.getDate();
          //     addProduct(
          //       values.productName,
          //       values.brand,
          //       values.price,
          //       values.subcategory_id,
          //       values.product_detail,
          //       date.toString(),
          //       values.quantity
          //     );
          //   }}
        >
          <Form>
            <MySelect label="Subcategory Type" name="subcategory_id">
              <option>Select a sub-category type</option>
              {/* {data.map((subCategory) => (
                <option
                  key={subCategory.subcategory_id}
                  value={subCategory.subcategory_id}
                >
                  {subCategory.subcategory_name}
                </option>
              ))} */}
            </MySelect>
            <br />

            <MyTextInput
              label="Product Name"
              name="productName"
              type="text"
              placeholder="Product Name"
            />

            <MyTextInput
              label="Brand"
              name="brand"
              type="text"
              placeholder="Brand"
            />
            <MyTextInput
              label="Price"
              name="price"
              type="number"
              placeholder="Price"
            />

            <MyTextInput
              label="Quantity"
              name="quantity"
              type="number"
              placeholder="Quantity"
            />

            <MyTextInput
              label="Description"
              name="product_detail"
              type="text"
              placeholder="Description"
            />

            <br />
            <div class="d-grid gap-2 d-md-flex justify-content-md-center">
              <button type="submit" className="saveaddproduct">
                Save
              </button>
            </div>
          </Form>
        </Formik>
      </div>{" "}
      <br /> <br />
    </div>
  );
}
